import React from 'react';
import axios from "axios";

class Courses extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            courseList: []
        };

    }

    // Fetch the list on first mount of the component
    componentDidMount() {
        this.getList();
    }

    // Retrieves the list of items from the Express app
    getList = () => {
        axios.get('http://localhost:8000/manabu/api/v1/course')
            .then(jsonResp => {
                console.dir(jsonResp);
                this.setState({ courseList: jsonResp.data.data})
            })
            .catch((err)=>{console.dir(err)})
    }
    render(){
        const courses = this.state.courseList.map((course,key) =>
            <li key={course.id}>{course.name}</li>
        );
        return (<div>
            <ul>
                {courses}
            </ul>
            <div>
                {this.state.courseList && this.state.courseList.length >0 ?  this.state.courseList[0].name:""}
            </div>

        </div>)
    }
}

export default Courses;
