import React from 'react';
import logo from './logo.svg';
import './App.css';
import Header from './components/common/header';
import CourseCard from './components/CourseCard';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.handleOnClick = this.handleOnClick.bind(this);
        this.state = {
            isClicked: false,
            courseList: []
        };

    }

    handleOnClick() {
        this.setState({isClicked: true});
        //window.location = 'https://en.wikipedia.org/wiki/Lizard';
    }

    componentWillMount(){
        setTimeout(()=>{
            this.setState({isClicked: true})
        },1000);
    }

    // Fetch the list on first mount
    componentDidMount() {
        this.getList();
    }

    // Retrieves the list of items from the Express app
    getList = () => {
        fetch('/manabu/api/v1/course')
            .then(res => res.json())
            .then(jsonResp => this.setState({ courseList: jsonResp.data}))
    }

    render() {
        return (
            <div>
                <Header name="Manabu"/>
                <div>
                    {this.state.courseList && this.state.courseList.length >0 ?  this.state.courseList[0].name:""}
                    {this.state.isClicked ?
                        <div>gone girl </div> :
                        <CourseCard handleOnClick={this.handleOnClick}></CourseCard>}
                </div>
            </div>
        )
    }
}

export default App;
